targets.expr:
round(
  100 *
    sum(
      rate(container_cpu_usage_seconds_total{image!="", container=~"$server",namespace="$namespace"}[5m])
    ) by (pod,container)
      /
    sum(
      container_spec_cpu_quota{image!="", container=~"$server",namespace="$namespace"} / container_spec_cpu_period{image!="", container=~"$server",namespace="$namespace"}
    ) by (pod,container)
)
